class Cher

    def 💕
        puts emoji + "  THERE'S ONLY" + emoji + " LOVE&FEAR "

        if(ARGV.count > 0) then
            str = ARGV.join(" ")
        else
            puts "💯 SAY💬 someTHING ⁉️🙋"
            str = gets.chomp
            system("clear")
        end

        words = str.split(" ")
        output = ''

        words.each do |word|
            if rand(10) > 1
                word = word.upcase
            end

            if rand(4) > 1
                output << word + emoji + ' '
            else
                output << word + ' '
            end
        end

        output =  "✨ " + output + ' 💕'
        bar = "███████████████████████████████████████████████████████████████████████"
        puts "#{bar} \r\n\r\n #{output} \r\n\r\n #{bar}"
        pbcopy(output)
    end

    def pbcopy(input)
        str = input.to_s
        if RUBY_PLATFORM == "x86_64-linux"
            system("echo #{str} | xclip -selection clipboard")
        else
            IO.popen('pbcopy', 'w') { |f| f << str }
        end
    end

